mapper.insert:
insert into attachment(
      attachment_no
    , media_type
    , path
    , category
    , parent_pk_no
    , original_name
    , stored_name
    , size
) values (
      #{attachmentNo}
    , #{mediaType}
    , #{path}
    , #{category}
    , #{parentPkNo}
    , #{originalName}
    , #{storedName}
    , #{size}
)

mapper.select:
select *
from attachment
where category    = #{category}
  and parent_pk_no = #{parentPkNo}
order by attachment_no asc

mapper.delete:
delete attachment
where category    = #{category}
  and parent_pk_no = #{parentPkNo}
