mapper.insert:
    insert into bid(
		bid_no, product_no, bidder_no, amount   
    ) values (
		#{bidNo}, #{productNo}, #{bidderNo}, #{amount}
    )

mapper.select:
select *
from bid
		where product_no = #{productNo}
order by bid_no desc;

select * 
from bid
	where product_no = #{productNo}
order by amount desc, bid_no asc;

   		select *
		from (
    		select *
    		from bid
    		where 
    			product_no = #{productNo}
    		order by 
    			amount desc, bid_no asc
		)
		where rownum = 1

select count(*) 
from bid 
where product_no = #{productNo}

select * 
from(
	select rownum rn, TMP.* 
	from(
		select * 
		from bid
		where product_no = #{productNo}
		order by amount desc, bid_no asc
	)TMP
) where rn between #{begin} and #{end}

select bidder_no
from (
    select bidder_no
    from bid
    	where product_no = #{productNo}
    order by amount desc, bid_no asc
)
where rownum = 1

select count(distinct bidder_no)
from bid
where product_no = #{productNo}

	select *
	from bid
		where bidder_no = #{bidderNo}
  			and product_no = #{productNo}
	order by bid_no desc
