mapper.insert:
insert into category(
      category_code
    , parent_code
    , name
    , depth
) values(
      #{categoryCode}
    , #{parentCode}
    , #{name}
    , #{depth}
)

mapper.update:
update category
set
      parent_code = #{parentCode}
    , name        = #{name}
    , depth       = #{depth}
where category_code = #{categoryCode}
