      payment_no
    , member_no
    , type
    , pg
    , pg_tid
    , amount
    , point
    , status
    , req_time
    , res_time
) values (
      #{paymentNo}
    , #{memberNo}
    , #{type}
    , #{pg}
    , #{pgTid}
    , #{amount}
    , #{point}
    , #{status}
    , #{reqTime}
    , #{resTime}
)
