mapper.insert:
insert into product(
	  product_no, seller_no, name, category_code, description
	, start_price, current_price, instant_price, start_time, end_time
) values (
	  #{productNo}, #{sellerNo}, #{name}, #{categoryCode}, #{description}
	, #{startPrice},  #{currentPrice}, #{instantPrice}, #{startTime}, #{endTime}
)

mapper.update:
update product
set
	  name = #{name}
	, category_code = #{categoryCode}
	, description = #{description}
	, start_price = #{startPrice}
	, final_price = #{finalPrice}
	, instant_price = #{instantPrice}
	, start_time = #{startTime}
	, end_time = #{endTime}
	, status = #{status}
	, buyer_no = #{buyerNo}
where product_no = #{productNo}

update product
name = #{name}, category_code = #{categoryCode}, description = #{description}, start_price = #{startPrice}, final_price = #{finalPrice}, instant_price = #{instantPrice}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}, buyer_no = #{buyerNo},
where product_no = #{productNo}

mapper.select:
select * from (
	select TMP.*, row_number() over(order by product_no desc) rn
	from product TMP
)
where rn between #{begin} and #{end}

mapper.update:
update product
set final_price = #{bidAmount}, buyer_no = #{bidMemberNo}, status = 'BIDDING'
where product_no = #{productNo}

update product
set status = 'CLOSED', ended_time = systimestamp
where product_no = #{productNo}

update product 
set
	current_price = #{currentPrice}
where product_no = #{productNo}

update product
set status = 'ENDED', final_price = #{finalPrice}, buyer_no = #{buyerNo}, ended_time = systimestamp
where product_no = #{productNo}

mapper.select:
select seller_no
from product
where product_no = #{productNo}

select count(*)
from product
where seller_no = #{sellerNo}

select *
from (
	select TMP.*, row_number() over(order by product_no desc) rn
	from product TMP
	where seller_no = #{sellerNo}
)
where rn between #{begin} and #{end}

select *
from (
	select TMP.*, row_number() over(order by product_no desc) rn
	from product TMP
	where status = 'BIDDING'
)
where rn between #{begin} and #{end}

select *
from (
  select TMP.*, row_number() over(
      ORDER BY 


              COALESCE(final_price, start_price) DESC 


              COALESCE(final_price, start_price) ASC 


              start_time DESC 


              end_time ASC 


  ) rn
  from product TMP

)
where rn between #{begin} and #{end}
